import React from 'react';
import { Link } from 'react-router-dom';
import './Showcase.css';
import BtnDefault from './buttons/BtnDefault';
import styled, { css } from 'styled-components';

const Showcase = ({ title, desc, bg, img }) => {
	return (
		<div
			class='flex justify-center items-center bg-center bg-cover bg-no-repeat h-64 lg:h-screen'
			style={{
				backgroundColor: bg,
				backgroundImage: img
					? `linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url(${img})`
					: '',
			}}
		>
			<div class='py-6'>
				<h1 class='underline'>{title}</h1>
				<p>{desc}</p>
				<Link to='./About'>
					<BtnDefault>Learn More</BtnDefault>
				</Link>
			</div>
		</div>
	);
};

export default Showcase;

