import React, { useState } from 'react';
import NavbarLinks from './NavbarLinks';
import DesktopLinks from './DesktopLinks';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';
import styled from 'styled-components';

const Navbar = () => {
	const [isActive, setIsActive] = useState(false);

	const handleClick = () => {
		setIsActive((prevState) => !prevState);
	};

	const DesktopLinks = (
		<NavList>
			<li className='menuBtn' onClick={this.props.handleClick}>
				<a>{element}</a>
			</li>
			<div id='myLinks'>
				<li>
					<Link to='/'>Home</Link>
				</li>
				<li>
					<Link to='../About'>About</Link>
				</li>
				<li>
					<Link to='../Menu'>Menu</Link>
				</li>
				<li>
					<Link to='../Contact'>Contact</Link>
				</li>
			</div>
		</NavList>
	);

	const MobileLinks = (
		<div id='myMobileLinks'>
			<NavList>
				<li>
					<Link to='/' onClick={this.props.handleClick}>
						Home
					</Link>
				</li>
				<li>
					<Link to='../About' onClick={this.props.handleClick}>
						About
					</Link>
				</li>
				<li>
					<Link to='../Menu' onClick={this.props.handleClick}>
						Menu
					</Link>
				</li>
				<li>
					<Link to='../Contact' onClick={this.props.handleClick}>
						Contact
					</Link>
				</li>
			</NavList>
		</div>
	);

	return (
		<nav className='navbar'>
			<DesktopLinks handleClick={handleClick} />
			{isActive && <NavbarLinks handleClick={handleClick} />}
		</nav>
	);
};

export default Navbar;

export const NavList = styled.ul`
	background-color: #304050;
	font-family: 'Roboto';
	z-index: 100;
	display: flex;
	justify-content: center;
	align-items: center;
	list-style-type: none;
	color: white;
	width: 100%;

	li {
		transition: 0.2s;
		font-size: 0.9rem;
		cursor: pointer;

		&:hover {
			background-color: #203040;
		}

		a {
			text-decoration: none;
			color: white;
			font-size: 1.2rem;
			padding: 1rem 1rem;
		}
	}
`;
