import React, { Component } from 'react';
import Column from '../components/Column';
import PropTypes from 'prop-types';

import { connect } from 'react-redux';

import './Menu.css';

export class Menu extends Component {
	componentDidMount() {
		this.props.getFoods();
	}

	render() {
		const { foods } = this.props.food;

		return (
			<div>
				<Column
					title='Menu'
					desc="With our wide selection, you're sure to find something you like!"
					bg='#655089'
				/>
				<ul className='menuUl'>
					{foods.map((food) => (
						<li>
							<h2>{food.name}</h2>
							<img src={food.img} alt='yup' />
						</li>
					))}
				</ul>
			</div>
		);
	}
}

Menu.propTypes = {
	food: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
	food: state.food,
});

export default connect(mapStateToProps, { getFoods })(Menu);
